#include <stdio.h> #define MAX_COUNT 5 // 친구 등록 가능한 최대 수 typedef char NAME_TYPE[14];
/// MAX_COUNT 만큼의 친구 이름을 저장하기 위한 name배열은 char name[MAX_COUNT][14]; 처럼 2차원 배열로 선언해서 사용한다.
/// name 배열을 다른 함수에서 포인터로 받으려면 char (*p_name)[14]; 처럼 사용해야 하는데 형태가 복잡하기 때문에
/// 이름을 위한 자료형 NAME_TYPE을 재정의한다. int AddFriend(NAME_TYPE *p_name, unsigned short int *p_age, float *p_height, float *p_weight, int count) { /// AddFriend는 친구를 추가하는 함수로 성공하면 1을 반환하고 실패하면 0을 반환한다. /// main함수에서 선언한 name, age, height, weight 배열의 시작 주소를 포인터로 받아서 사용하며 /// count는 현재까지 입력된 친구의 수를 기업하는 변수이다. if (count < MAX_COUNT) { // 입력 가능한 최대 수를 넘었는지 체크함 printf("
새로운 친구 정보를 입력하세요
"); printf("1. 이름 :"); scanf("%s", *(p_name + count)); // name 배열의 count 위치에 이름을 입력함 printf("2. 나이 :"); scanf("%hu", p_age + count); // age 배열의 count 위치에 나이를 입력함 printf("3. 키 :"); scanf("%f", p_height + count); // height 배열의 count 위치에 키를 입력함 printf("4. 몸무게 :"); scanf("%f", p_weight + count); // weight 배열의 count 위치에 몸무게를 입력함 printf("입력을 완료했습니다.

"); return 1; // 친구 추가 성공하면 1을 반환함 } else { // 입력 가능한 최대 수를 넘었을 때 오류를 출력함 printf("최대 인원을 초과하여 입력을 할 수 없습니다.
"); printf("최대 %d명까지만 관리 가능합니다.

", MAX_COUNT); } return 0; // 친구 추가 실패
} void ShowFriendList(NAME_TYPE *p_name, unsigned short int *p_age, float *p_height, float *p_weight, int count) { /// ShowFriendList는 등록된 친구를 출력하는 함수로 /// main함수에서 선언한 name, age, height, weight 배열의 시작 주소를 포인터로 받아서 사용한다. /// count는 현재까지 입력된 친구 수를 기억하는 변수이다. int i; if (count > 0) { // 등록된 친구가 있으면 그 수만큼 반복하면서 친구 정보를 출력함 printf("
등록한 친구 목록
"); printf("===================================
"); for (i = 0; i < count; i++) { printf("%-14s, %3d, %6.2f, %6.2f
", *(p_name + i), *(p_age + i), *(p_height + i), *(p_weight + i)); } printf("===================================

"); } else { // 등록한 친구가 없으면 오류를 출력함 printf("
등록된 친구가 없습니다.

"); }
} void main() { NAME_TYPE name[MAX_COUNT]; // 친구 이름을 저장할 배열 unsigned short int age[MAX_COUNT]; // 친구 나이를 저장할 배열 float height[MAX_COUNT]; // 친구 키를 저장할 배열 float weight[MAX_COUNT]; // 친구 몸무게를 저장할 배열 int count = 0, num; // count는 등록된 친구 수 while (1) { // 무한 루프: 사용자가 3을 누르면 break문으로 종료시킴 // 메뉴를 화면에 출력함 printf(" [ 메뉴 ] 
"); printf("=================
"); printf("1. 친구 추가 
"); printf("2. 친구 목록 보기 
"); printf("3. 종료 
"); printf("=================
"); printf("번호 선택 :"); scanf("%d", &num); // 사용자에게 번호를 입력 받음 if (num == 1) { // 1번: 친구 추가를 선택한 경우 if (1 == AddFriend(name, age, height, weight, count)) count++; /// AddFriend 함수가 1을 반환하면 정상적으로 친구 정보가 추가된 것이다. /// 따라서 1을 반환했을 때만 등록된 친구 수를 증가시킨다. /// 이름, 나이, 키, 몸무게 배열의 주소를 넘겨준다. } else if (num == 2) { // 2번: 친구 목록 보기를 선택한 경우 ShowFriendList(name, age, height, weight, count); /// ShowFriendList 함수에 이름, 나이, 키, 몸무게 배열의 주소를 인수로 넘겨준다. } else if (num == 3) { // 3번: 반복문을 빠져나가 종료함 break; } else { // 번호가 유효하지 않은 경우에 오류 메시지를 출력함 printf("1~3 번호만 선택할 수 있습니다!!

"); } }
} 사용자 이름을 재정의한 NAME_TYPE에 대해 좀 더 알아보자 이 프로그램에서 사용자 이름은 영문 기준 최대 13자까지 저장할 수 있게 만들었다. 그런데 문자열 끝에 NULL문자 0까지 저장해야 하므로 배열의 크기는 14가 된다. 이름은 문자열 형태이기 때문에 이미 1차원 배열 형식이다. 그래서 다섯 명의 이름 데이터(MAX_COUNT)를 저장하려면 다음과 같이 2차원 배열로 선언해서 사용해야 한다. char name[MAX_COUNT][14]; 그리고 name배열을 다른 함수에서 포인터로 받으려면 다음과 같은 형식의 포인터를 사용해야 한다. char (*p_name)[14]; typedef를 사용하여 이름을 위한 자료형(NAME_TYPE)을 만들고 표현을 단순화 시켜보자. char name[MAX_COUNT][14]; -> NAME_TYPE name[MAX_COUNT];
char (*p_name)[14]; -> NAME_TYPE *p_name;

308.body:
과거에는 컴퓨터 시스템의 메모리 용량이 적어서 프로그래머들은 메모리를 최대한 적게 사용하도록 프로그램을 개발해야 했다. 최근에는 컴퓨터 시스템의 메모리 용량이 점차 늘어나면서 메모리를 조금 더 사용하더라도 프로그램의 실행 속도가 향상되도록 프로그램을 개발하고 있다. 그런데 구조체의 경우에는 다양한 크기의 메모리를 하나의 그룹으로 묶어 사용하다 보니 구조체 요소를 사용할 때 실행 속도가 떨어지는 문제가 있다. 그래서 구조체의 요소를 일정한 크기로 정렬하여 실행 속도를 더 빠르게 하는 개념이 C 언어 컴파일러에 추가되었다. 컴파일러마다 용어의 차이는 있지만 마이크로소프트에서 제공하는 C 컴파일러의 경우에는 '구조체 멤버 정렬'(Struct Member Alignment) 기능을 제공하며 1, 2, 4, 8바이트로 정렬 기준을 설정할 수 있다. 18-2에서 구조체로 만든 자료형의 크기는 구조체를 구성하는 요소들의 크기를 합산한 것과 같다고 설명했다. 하지만 실제로는 컴파일러에서 구조체 정렬 기준을 어떻게 설정하는 지에 따라 구조체로 만든 자료형의 크기가 달라질 수 있다. 그래서 이 기능을 모르고 단순히 구조체를 구성하는 요소의 크기를 합산해서 구조체 크기로 사용하다가 버그가 발생하는 경우도 있다. 구조체 멤버 정렬 기준에 따라 구조체로 선언한 자료형의 크기가 어떻게 달라지는지 설명하기 위해 아래와 같이 Test구조체를 선언하고, Test자료형의 크기가 어떻게 달라지는지 알아보자. struct Test { char a; // 1바이트 int b; // 4바이트 short c; // 2바이트 char d; // 1바이트
}; 1바이트 정렬 이 정렬을 사용하면 구조체의 본래 의미대로 메모리가 구성된다. 따라서 이 기준으로 정렬된 Test자료형의 크기는 8바이트이다. 구조체 멤버 정렬 1바이트 2바이트 정렬 각 요소는 2의 배수에 해당하는 주소에서 시작할 수 있고 전체 크기가 2의 배수가 되어야 한다. 따라서 요소가 놓일 주소가 2의 배수가 아니라면 해당 1바이트를 버리고 2의 배수가 되는 주소에 놓인다. 하지만 예외적으로 요소의 자료형이 2바이트보다 작은 경우에는 해당 요소의 크기로 정렬된다. 예를 들어 2, 4, 8바이트 자료형의 요소들은 2의 배수에 해당하는 주소에 배치되지만 2바이트보다 작은 1바이트 자료형 요소들은 그대로 1바이트로 정렬된다. 위와 같은 기준을 적용하면 전체 크기는 9바이트가 되어야 하지만, 2바이트 정렬은 전체 크기가 2의 배수가 되어야 하기 때문에 10바이트가 된다. 그리고 마지막 1바이트를 사용하지 않는다. 결론적으로 2바이트 정렬 기준으로 정렬한 Test구조체의 크기는 10바이트이다. 구조체 멤버 정렬 2바이트 4바이트 정렬 각 요소는 4의 배수에 해당하는 주소에서 시작할 수 있고 전체 크기가 4의 배수가 되어야 한다. 따라서 요소가 놓일 주소가 4의 배수가 아니라면 해당 1~3바이트를 버리고 4의 배수가 되는 주소에 놓인다. 하지만 요소의 자료형이 4바이트보다 작은 경우에는 해당 요소의 크기로 정렬된다. 예를 들어 4바이트와 8바이트 자료형의 요소들은 4의 배수에 해당하는 주소에 배치되지만 4바이트보다 작은 1바이트 자료형은 1바이트 정렬이 적용되고 2바이트 자료형은 2바이트 정렬이 적용된다. 위와 같은 기준을 적용하면 전체 크기는 11바이트가 되어야 하지만, 4바이트 정렬은 전체 크기가 4의 배수가 되어야 하기 때문에 12바이트가 된다. 그리고 마지막 1바이트를 사용하지 않는다. 결론적으로 4바이트 정렬 기준으로 정렬한 Test구조체의 크기는 12바이트이다. 구조체 멤버 정렬 4바이트 8바이트 정렬 구조체를 정렬할 때 모든 요소가 기준 정렬 바이트 보다 작으면 요소 중에서 가장 큰 요소의 크기로 정렬된다. 따라서 지금 예시로 사용하는 구조체는 가장 큰 요소의 크기가 4바이트이기 때문에 8바이트 정렬을 사용해도 4바이트로 정렬되어 버린다. 따라서 다음과 같이 Test구조체에서 8바이트 요소를 사용하도록 수정해보자. struct Test { char a; // 1바이트 double b; // 8바이트 short c; // 2바이트 char d; // 1바이트
}; 각 요소는 8의 배수에 해당하는 주소에서 시작할 수 있고 전체 크기가 8의 배수가 되어야 한다. 따라서 요소가 놓일 주소가 8의 배수가 아니라면 해당 1~7바이트를 버리고 8의 배수가 되는 주소에 놓인다. 하지만 요소의 자료형이 8바이트보다 작은 경우에는 해당 요소의 크기로 정렬된다. 예를 들어 8바이트 자료형 요소들은 8의 배수에 해당하는 주소에 배치되지만 8바이트보다 작은 1, 2, 4바이트 자료형은 각각 1, 2, 4바이트 정렬이 적용된다. 위와 같은 기준을 적용하면 전체 크기는 19바이트가 되어야 하지만, 8바이트 정렬은 전체 크기가 8의 배수가 되어야 하기 때문에 크기는 24바이트가 된다. 그리고 마지막 5바이트를 사용하지 않는다. 결론적으로 8바이트 정렬 기준으로 정렬된 Test구조체의 크기는 24바이트이다. 구조체 멤버 정렬 8바이트 마지막에 설명한 8바이트 정렬의 예시를 보면 알 수 있듯이 이 구조체는 24바이트 중에 무려 12바이트나 버려진다. 그런데 놀랍게도 요즘 컴파일러들은 8바이트 정렬을 기본 값으로 하고 있다. 따라서 구조체에 8바이트 크기의 자료형을 사용하지 않았다면 낭비가 적겠지만, double이나 __int64같은 8바이트 크기의 자료형을 사용하는 순간 구조체 크기가 갑자기 커지게 될 것이다.

309.body:
이 문제는 구조체로 자료형을 선언할 때 같은 크기의 요소들끼리 모아 주는 것만으로도 프로그램의 효율을 크게 높일 수 있다. /* 기존 구조체 - A */ struct Test { char a; // 1바이트 double b; // 8바이트 short c; // 2바이트 char d; // 1바이트
}; /* 개선된 구조체 - B */ struct Test { char a; // 1바이트 char d; // 1바이트 short c; // 2바이트 double b; // 8바이트
}; 다음은 개선된 구조체(B)의 메모리 배치도이다. 그림을 보면 구조체의 크기가 16바이트이다. 단순히 구조체 요소의 순서만 변경했을 뿐인데 낭비되던 메모리가 12바이트에서 4바이트로 줄어들었다. 구조체 요소 모아준 경우 이처럼 작은 부분이라도 정확하게 개념을 이해해서 소스 코드를 구성하면 특별한 최적화 작업을 해주지 않아도 프로그램의 메모리 사용 효율을 높일 수 있다. 그리고 앞에서 설명한 것처럼 컴파일러 설정에 따라서 구조체로 선언한 자료형의 크기가 바뀔 수 있다. 따라서 동적 메모리 할당을 할 때 구조체의 크기를 직접 계산해서 사용하는 것보다 자료형의 크기를 계산해 주는 sizeof연산자를 사용하는 것이 안전하다. struct Test *p1 = (struct Test *) malloc(16); // 설정에 따라 오류가 발생할 수 있음 struct Test *p2 = (struct Test *) malloc(sizeof(struct Test)); // 권장하는 형태

314.body:
연결 리스트에 노드가 하나도 없는 경우(p_head가 NULL인 경우)를 체크하지 않으면 문제가 발생할 수 있다. 따라서 p_head가 NULL인지 여부를 체크해서 처음 노드를 만드는 작업인지를 구별해야 한다. 연결 리스트에 노드를 추가하는 작업은 이후에도 계속해서 반복적으로 일어나는 작업이기 때문에 이 작업을 함수로 만들어 보자. 다음에 정의한 AddNumber함수는 연결 리스트에 새로운 노드를 추가하고 data변수에 넘긴 숫자 값을 새로 추가된 노드의 number에 저장하도록 구성한다. // 기존 연결 리스트에 노드가 없는 경우 이 함수로 전달된 p_head값을 수정해야 하므로
// AddNumber를 호출할 때 p_head 포인터 변수의 주소 값을 넘겨준다.
// 따라서 1차원 포인터의 주소 값을 받아서 사용해야 하므로 2차원 포인터를 사용한다.
void AddNumber(Node **pp_head, int data) { NODE *p; // *pp_head는 AddNumber 함수를 호출할 때 전달 받은 p_head포인터에 저장된 주소 값을 의미한다. if(NULL != *pp_head) { // pp_head를 사용해 값을 변경하면 p_head값이 수정된다. // p_head는 시작 노드를 기억하고 있어야 하므로 새로운 포인터에 p_head 주소 값을 받아 탐색을 진행한다. p = *pp_head; // 마지막 노드를 찾기 위해서 p_next가 NULL일 때까지 반복함 while(NULL != p->p_next) p = p->p_next; p->p_next = (NODE *) malloc(sizeof(NODE)); // 새 노드를 위한 메모리를 할당함 p = p->p_next; // 새로 만든 노드의 주소 값을 p에 넣음 } else { // p_head 값이 NULL이라서 첫 노드가 추가됨. p_head 값에 직접 대입함 *pp_head = (NODE *) malloc(sizeof(NODE)); // 새 노드를 위한 메모리를 할당함 p = *pp_head; // 새로 만든 노드의 주소 값을 p에 넣음 } p->number = data; // 새 노드의 number에 data값을 저장함 p->p_next = NULL; // 다음 노드가 없음을 명시함
} 이렇게 AddNumber함수를 만들었다면 이 연결 리스트에 새로운 노드를 추가하고 값 15를 저장하고 싶을 때 다음과 같이 코드를 구성하면 된다. NODE *p_head = NULL;
AddNumber(&p_head, 15);

315.body:
앞에서 정의한 AddNumber함수의 단점은 노드가 추가될 때마다 마지막 노드를 찾기 위해 탐색을 해야 한다는 점이다. 노드가 많아질수록 이 작업은 점점 더 느리게 수행될 것이다. 그래서 연결 리스트는 시작 노드의 주소 값을 기억하는 헤드 포인터처럼 마지막 노드의 주소 값을 기억하는 '테일 포인터'(Tail Pointer)를 하나 더 사용한다. 그러면 새 요소가 추가될 때 마지막 노드를 찾기 위해 일일이 탐색하지 않고 테일 포인터가 가리키는 노드에 바로 추가할 수 있다. AddNumber함수에 테일 포인터 p_tail을 사용하도록 소스 코드를 수정해 보자. // 기존 연결 리스트에 노드가 없는 경우 이 함수로 전달된 p_head와 p_tail값을 수정해야 하므로
// AddNumber를 호출할 때 p_head, p_tail 포인터 변수의 주소 값을 넘겨준다.
// 따라서 1차원 포인터의 주소 값을 받아서 사용해야 하므로 두 개의 2차원 포인터를 사용한다.
void AddNumber(NODE **pp_head, NODE **pp_tail, int data) { if(NULL != *pp_head) { (*pp_tail)->p_next = (NODE *) malloc(sizeof(NODE)); // 새 노드의 메모리를 할당함 // *pp_tail이라고 사용하면 AddNumber를 호출할 때 사용한 p_tail 포인터에 // 저장된 주소 값을 의미한다. 이 값은 마지막 노드의 주소이다. *pp_tail = (*pp_tail)->p_next; // p_tail(*pp_tail)에 새 노드의 주소 값을 저장함 } else { // p_head 값이 NULL이라서 첫 노드가 추가됨. p_head에 직접 대입함 *pp_head = (NODE *) malloc(sizeof(NODE)); // 새 노드의 메모리를 할당함 *pp_tail = *pp_head; // 새 노드의 주소 값을 p_tail(*pp_tail)에 저장함 } (*pp_tail)->number = data; // 새 노드의 number에 data값을 저장함 (*pp_tail)->p_next = NULL; // 다음 노드가 없음을 명시함
} 위의 코드처럼 연결 리스트의 마지막 노드를 기억하는 테일 포인터를 사용하도록 변경하면 다음 그림처럼 테일 포인터가 항상 마지막 노드를 가리키게 된다. 연결 리스트 마지막 노드 기억하기 따라서 새로 추가한 노드(마지막 노드)의 주소 값을 p_tail포인터가 기억하므로 노드가 추가될 때마다 마지막 노드를 찾기 위해 탐색할 필요가 없다. 즉 앞에서 구성한 AddNumber함수에서는 마지막 노드를 찾기 위해 다음 반복문이 필요했지만 테일 포인터를 사용하도록 만든 AddNumber함수에서는 이 반복문이 사라졌다. 그래서 프로그램이 훨씬 효율적이 된다. while(NULL != p->p_next) p = p->p_next; // 마지막 노드를 탐색하는 반복문이 필요하지 않음

316.body:
이렇게 동적으로 할당된 노드들은 free함수를 호출하여 메모리를 해제할 때까지 유지된다. 따라서 프로그램이 끝날 때 동적으로 할당된 노드를 모두 제거하는 작업을 추가해야 한다. 이 작업은 연결 리스트를 구성하는 노드를 탐색하면서 하나씩 노드를 제거해야 하므로 앞에서 설명한 마지막 노드를 찾는 작업과 비슷하다. 즉 첫 노드부터 마지막 노드까지 반복하면서 노드의 주소 값을 저장하기 위해 할당된 메모리를 하나씩 해제하면 된다. 아래 코드처럼 너무 단순하게 생각해서 코드를 다음과 같이 구성하면 문제가 생긴다. NODE *p = p_head; // 시작 노드부터 마지막 노드까지 이동하도록 반복문을 구성함
while(NULL != p) { free(p); // 포인터 변수 p가 가리키는 노드를 삭제함 p = p->p_next; // 오류 발생: 다음 노드로 이동할 수 없음
} 앞의 코드는 포인터 변수 p를 사용해서 연결 리스트를 구성하는 첫 노드부터 마지막 노드까지 찾는 작업은 잘 구성하였다. 하지만 free함수를 호출한 시점에 문제가 있다. 왜냐하면 free(p);를 수행하면 포인터 변수 p가 가리키고 있던 메모리가 해제되는데 이 해제된 메모리에 다음 노드의 주소 값을 기억하고 있던 p_next도 포함되어 있기 때문이다. 즉 이미 해제된 메모리를 사용하려고 해서 오류가 발생한 것이다. 1. while 반복문 수행 전 상황 while 반복문 수행 전 상황 2. free(p);가 수행됨. 포인터 변수 p가 가리키는 대상이 메모리에서 해제됨 포인터 변수 p가 가리키는 대상이 메모리에서 해제됨 3. p = p->p_next;가 수행됨 포인터 p가 가리키던 대상 메모리가 해제되었기 때문에 108번지가 들어 있던 p_next를 사용할 수 없다. 이미 해제된 메모리를 사용하려니까 오류가 발생한다. 이 문제를 해결하려면 p가 가리키는 대상 메모리를 해제하기 전에 p->p_next에 저장된 주소값을 다른 포인터 변수로 옮겨 놓고 해제하면 된다. NODE *p = p_head, *p_save_next; // 시작 노드부터 마지막 노드까지 이동하도록 반복문을 구성함
while(NULL != p) { p_save_next = p->p_next; // p->p_next 값을 다른 포인터 변수에 보관함 free(p); // 포인터 변수 p가 가리키는 노드를 삭제함 p = p_save_next; // 다음 노드로 옮김
} // while문이 끝났다는 것은 연결 리스트의 모든 노드가 제거되었다는 뜻이다 p_head = p_tail = NULL; // 연결 리스트의 시작과 끝이 모두 없음을 명시함 헤드 포인터로 연결 리스트의 전체 노드를 제거할 수 있다 포인터를 두 개(p, p_save_next)나 추가로 선언해서 사용하는 것이 마음에 들지 않는다면 연결 리스트 구성을 위해 사용하고 있던 헤드 포인터를 활용해도 된다. 위의 예시 코드에서 p의 역할을 p_head포인터가 대신하고 p_save_next의 역할을 p가 대신하도록 만드는 것이다. NODE *p; // 시작 노드부터 마지막 노드까지 이동하도록 반복문을 구성함
while(NULL != p_head) { p = p_head->p_next; // p_head->p_next 값을 포인터 변수 p에 보관함 free(p_head); // 포인터 변수 p_head가 가리키는 노드를 삭제함 p_head = p; // 다음 노드로 옮김
} p_tail = p_head; // 반복문을 빠져나오면 p_head값은 NULL이므로 p_tail값도 NULL로 변경함

317.body:
여러 개의 숫자를 입력 받아서 합산하는 프로그램(16-5에서 작성)에 연결 리스트를 추가하여 소스 코드를 수정해 보자. 이렇게 작업하면 사용자에게 몇 개의 숫자를 사용할 것인지 묻지 않고 바로 사용자가 입력한 숫자들을 합산해서 출력할 수 있다. /* 사용자에게 숫자를 입력 받아 합산해 출력하기 */ #include <stdio.h>
#include <malloc.h> // malloc, free 함수를 사용하기 위해 추가함 typedef struct node { int number; // 정수 값을 저장할 변수 struct node *p_next; // 다음 노드를 가리킬 포인터
} NODE; /// 기존 연결 리스트에 노드가 없는 경우 이 함수로 전달된 p_head와 p_tail값을 수정해야 하므로
/// AddNumber를 호출할 때 p_head, p_tail 포인터 변수의 주소 값을 넘겨준다.
/// 따라서 1차원 포인터의 주소 값을 받아서 사용해야 하므로 두 개의 2차원 포인터를 사용한다.
void AddNumber(NODE **pp_head, NODE **pp_tail, int data) { /// AddNumber 함수를 호출할 때 전달 받은 p_head 포인터에 저장된 주소 값을 의미한다. /// 이 값이 NULL이면 기존 연결 리스트에 노드가 없다는 뜻이다. if (NULL != *pp_head) { /// *pp_tail 이라고 사용하면 AddNumber를 호출할 때 사용한 p_tail 포인터에 저장된 주소 값을 의미한다. /// 이 값은 마지막 노드의 주소이다. (*pp_tail)->p_next = (NODE *) malloc(sizeof(NODE)); // 새 노드를 할당함 *pp_tail = (*pp_tail)->p_next; // p_tail(*pp_tail)에 새 노드의 주소 값을 저장함 } else { // p_head값이 NULL이라서 첫 노드가 추가됨. p_head값에 직접 대입함 *pp_head = (NODE *) malloc(sizeof(NODE)); // 새 노드를 할당함 *pp_tail = *pp_head; // 새 노드의 주소 값을 p_tail(*pp_tail)에 저장함 } (*pp_tail)->number = data; // 새 노드의 number에 data값을 저장함 (*pp_tail)->p_next = NULL; // 다음 노드가 없음을 명시함
} void main() { // 노드의 시작과 끝을 기억할 포인터 NODE *p_head = NULL, *p_tail = NULL, *p; int sum = 0, temp; while (1) { // 무한 루프: 중간에 9999를 누르면 종료함 printf("숫자를 입력하세요(9999를 누르면 종료) :"); scanf("%d", &temp); if (9999 == temp) break; // 9999를 누르면 입력을 중단함 // 노드의 시작과 끝을 기억하는 포인터의 주소 값과 입력된 숫자를 전달함 AddNumber(&p_head, &p_tail, temp); } /// 입력된 숫자를 출력하기 위해서 노드를 탐색할 포인터에 시작 노드의 주소 값을 대입한다. /// p_head를 직접 사용하면 시작 노드의 위치를 잃어 버린다. p = p_head; while (NULL != p) { if (p != p_head) printf(" + "); // 숫자와 숫자 사이에 +를 출력함 printf(" %d ", p->number); // 입력한 숫자를 출력함 sum = sum + p->number; // 입력한 숫자들을 합산함 p = p->p_next; // 다음 노드로 이동함 } printf(" = %d\n", sum); // 합산 값을 출력함 while (NULL != p_head) { // p_head 값이 NULL이 될 때까지 반복함 p = p_head; // 현재 노드를 삭제하기 위해 p 변수에 노드 주소 값을 저장함 p_head = p_head->p_next; // 시작 위치를 다음 노드로 옮김 free(p); // 기억했던 주소를 사용하여 노드를 삭제함 } p_tail = p_head; // 반복문을 나오면 p_head 값은 NULL. p_tail 값도 NULL로 변경함
} 숫자를 입력하세요(9999를 누르면 종료) :5
